use std::collections::HashSet;

fn main() {
    let input = "";
    let chars = input.chars().collect::<Vec<char>>();

    let answer1 = search(&chars, 4);
    println!("Part 1: {:#?}", answer1);
    let answer2 = search(&chars, 14);
    println!("Part 2: {:#?}", answer2);
}

fn search(chars: &Vec<char>, marker_size: usize) -> Option<usize> {
    for i in 0..(chars.len() - (marker_size - 1)) {
        let qwe = chars[i..(i + marker_size)]
            .iter()
            .map(|c| c.clone())
            .collect::<HashSet<_>>();
        if qwe.len() == marker_size {
            return Some(i + marker_size);
        }
    }
    return None;
}